import { useSelector } from "react-redux"
import dynamic from "next/dynamic"
import { todayChart } from "../helpers/todayDate"
import generateDefaultDays from "../helpers/defaultDays"
import { render, useRef } from "react-dom"

const selectDays = state => state.categories.days
const Chart = dynamic(() => import ("react-apexcharts"), {ssr: false})

function CategoryChart(props) {

    const ref = useRef(true)
    const days = useSelector(selectDays)
    // Array of objects [{}, {}]
    const daysValues = Object.values(days)

    // get array of days' ids ["", ""], inputs: data - array of objects [{}, {}]
    const daysIds = daysValues.map((day) => day.id)

    const defaultDays = generateDefaultDays(new Date(daysIds[1]), new Date(todayChart))

    function cellHappenedToday(cell, defaultDay) {
        if (daysIds.includes(defaultDay)) {
            return daysValues.reduce((total, day) => {  // o=daysValues.reduce((total, day) => {...}, 0)
                if (day.id === defaultDay) {
                    const categoryIds = Object.values(day.categories).map((cat) => cat.id)
                    if (categoryIds.includes(props.id)) {
                        const cells = Object.values(day.categories[props.id].cells)
                        console.log("cells", cells, "cell", cell)
                        if (cells.includes(cell)) {
                            console.log(cells.includes(cell))
                            return total + 1
                        }
                    }
                }                
                return 0 
            }, 0)

        } else {
            return 0
        }
    }

    const options = {
        series: 
            props.defaultCells.map((cell) => (
            {
                name: cell.name,
                data: defaultDays.map((day) => (
                    {
                        x: day,
                        y: cellHappenedToday(cell, day)
                    }
                ))
            }
        )),
        plotOptions: {
            heatmap: {
                colorScale: {
                    ranges: [{
                        from: 0,
                        to: 0,
                        color: "#F9FAFB"
                    }, {
                        from: 1,
                        to: 1,
                        color: props.color
                    }]
                }
            }
        }
    }

    // var chart = new ApexCharts(ref, options)
    // chart.render()
    return (
        <div>
            <Chart options={options.plotOptions} series={options.series} type="heatmap" width="500"/>
        </div>
    )
}

export default CategoryChart

