            "mood": {
                id: "mood",
                cells: []
            }
        },
        allIds: {
            ["food", "mood"]
        }
    },
    cells: {
        byId: {
            "bread": {
                id: "bread",
                category: "food"
            },
            "happy": {
                id: "happy",
                category: "mood"
            }
        },
        allIds: {
            ["bread", "happy"]
        }
    }
} */

