const initialState = {
    days: {
        "0": {
            categories: {
                "test": {
                    cells: ["test"]
                }
            }
        }
    },
    cells: {
        "test": {
            category: "test",
            days: ["0"]
        }
    }
}

function addDay(state, action) {
    const { payload } = action
    const { dayId, categoryName, cellName } = payload
    return {
        ...state,
        [dayId]: {
            categories: {
                [categoryName]: {
                    cells: [cellName]
                }
            }
        }
    }
}

function addCategory(state, action) {
    const { payload } = action
    const { dayId, categoryName, cellName } = payload
    return {
        ...state,
        days: {
            ...state.days,
            [dayId]: {
                ...state.days[dayId],
                categories: {
                    ...state.days[dayId].categories,
                    [categoryName]: {
                        cells: [cellName]
                    }
                }
            }
        }
    }
}

function addCell(state, action) {
    const { payload } = action
    const { dayId, categoryName, cellName } = payload
    return {
        ...state,
        days: {
            ...state.days,
            [dayId]: {
                ...state.days[dayId],
                categories: {
                    ...state.days[dayId].categories,
                    [categoryName]: {
                        ...state.days[dayId].categories[categoryName],
                        cells: [
                            ...state.days[dayId].categories[categoryName].cells,
                            cellName
                        ]
                    }
                }
            }
        }
    }
}

