export default function(state = initialState, action) {
    switch(action.type) {
        case ADD_CELL: {
            return {
                ...state,
                days: {
                    ...state.days,
                    [action.payload.day_id]: {
                        ...state.days[action.payload.day_id] || {},
                        categories: {
                            ...state.days[action.payload.day_id]?.categories || {},
                            [action.payload.category_name]: {
                                ...state.days[action.payload.day_id].categories[action.payload.category_name],
                                cells: [
                                    ...state.days[action.payload.day_id].categories[action.payload.category_name].cells,
                                    action.payload.cell_name
                                ]
                            }
                        }
                    }
                }
            }
        }

        default:
            return state
    }
}
