import { ADD_DAY, ADD_CATEGORY, ADD_CELL, REMOVE_CELL } from "../actionTypes"

/* STRUCTURE OF THE STATE

state = {
    days: [
        {
            day_id: "",
            categories: [
                {
                    category_name: "",
                    cells: []
                }
            ]
        }
    ]
} */

const initialState = {
    days: []    
}

export default function(state = initialState, action) {
    switch(action.type) {
        case ADD_CELL: {
            return {
                ...state,
                days: [
                    ...state.days,
                    {
                        day_id: action.payload.day_id,
                        categories: [
                            ...state.categories,
                            {
                                category_name: action.payload.category_name,
                                cells: [
                                    ...state.cells,
                                    action.payload.cell_name
                                ]
                            }
                        ]
                    }
                ]
            }
        }
        case REMOVE_CELL: {
            return {
                ...state,
                days: [
                    ...state.days,
                    {
                        day_id: action.payload.day_id,
                        categories: [
                            ...state.categories,
                            {
                                category_name: action.payload.category_name,
                                cells: [
                                    ...state.cells,
                                    action.payload.cell_name
                                ]
                            }
                        ]
                    }
                ]
            }
        }

        default:
            return state
    }
}
