export default function chartOptions(categoryColor) {

    const options = {
        chart: {
            toolbar: {
                show: false
            },
        },
        legend: {
            show: true
        },
        dataLabels: {
            enabled: false
        },
        plotOptions: {
            heatmap: {
                radius: 100,
                colorScale: {
                    ranges: [{
                        from: 0,
                        to: 0,
                        color: "#F9FAFB"
                    }, {
                        from: 1,
                        to: 2,
                        color: "#FDE68A"
                    }]
                }
            }
        }
    }
}
