import simplifyDate from "../helpers/simplifyDate"
import { todayChart } from "../helpers/todayDate"
import { storeDaysIds } from "./storeDays"

function generateDefaultDays(firstDay, today) {
    for (var defaultDays=[], dt=new Date(firstDay); dt<=today; dt.setDate(dt.getDate()+1)) {
        defaultDays.push(new Date(dt))
    }
    const days = defaultDays.map((day) => (day.getFullYear()).toString() + "-" + (((day.getMonth()+1) < 10 ? "0" : "") + (day.getMonth()+1).toString()) + "-" + (((day.getDate() < 10 ? "0" : "") + (day.getDate()).toString())))
    return days
}

// get array of dates from second day to today ["2021-1-1", "2021-1-2"]
const chartDays = generateDefaultDays(new Date(storeDaysIds[0]), new Date(todayChart))

// list of columns to chart filled with 0, every cell chart gets it
const defaultDatas = chartDays.map((day) => {
    return {
        x: simplifyDate(day),
        y: 0
    }
})
