import { useSelector } from "react-redux"
//import { ApexCharts } from "apexcharts"
import { todayChart } from "../helpers/todayDate"

const selectDays = state => state.categories.days

function CategoryChart(props) {

    const days = useSelector(selectDays)
    // Array of objects [{}, {}]
    const daysValues = Object.values(days)

    // get array of days' ids ["", ""], inputs: data - array of objects [{}, {}]
    const daysIds = daysValues.map((day) => day.id)

    function generateDefaultDays(firstDay, today) {
        for (var defaultDays=[], dt=new Date(firstDay); dt<=today; dt.setDate(dt.getDate()+1)) {
            defaultDays.push(new Date(dt))
        }
        const days = defaultDays.map((day) => (day.getDate()).toString() + "." + (day.getMonth()+1).toString() + "." )
        return days
    }

    const defaultDays = generateDefaultDays(new Date(daysIds[1]), new Date(todayChart))


    /////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

    // get array of categories of particular day ["", ""], inputs: day - string, data - array of objects [{}, {}]
    function getCategoriesofDay(day, data) {
        let categories = []
        data.forEach((item) => {
            if (item.id === day) {
                categories = Object.values(item.categories).map((cat) => cat.id)
            }
        })
        return categories
    }

    // get array of cells of particular category ["", ""], inputs: day - string, category - string, data(category) - array of objects [{}, {}]
    function getCellsofCategory(category, data) {
        let cells = []
        data.forEach((item) => {
            if (item.id === category) {
                cells = Object.values(item.cells)
            }
        })
        return cells
    }

    // Array of arrays of objects - categories [[{}, {}], [{}, {}]]
    //const categoriesValues = daysValues.map((day) => Object.values(day.categories))
    //console.log(categories)

    // Array of arrays of ids [["", "", ""], ["", "", ""]]
    //const categoriesIds = categories.map((category) => category.map((cat) => cat.id))
    //console.log(categoriesIds)

    // Array of arrays of arrays of cells [[["",""],["",""]],[["",""],["",""]]]
    //const cells = categories.map((category) => category.map((cat) => cat.cells))
    //console.log(cells)

    // function getDefaultCell(defaultCells) {
    //     defaultCells.forEach((cell) => cell.name)
    // }

    function cellHappenedToday(cell, cells, category, days, defaultday) {
        if (days.includes(defaultday) && category.includes(props.id) && cells.includes(cell)) {
            return 1
        } else {
            return 0
        }  
    }

    const y = cellHappenedToday("sad", getCellsofCategory(props.id, getCategoriesofDay(daysIds[1], daysValues)), getCategoriesofDay(daysIds[1], daysValues), daysIds, daysIds[1])
    console.log(y)

    // const options = props.defaultCells.map((cell) => {
    //     name: cell.name,
    //     data: defaultDays.map((day) => {
    //         x: day,
    //         y: cellHappenedToday(cell, getCellsofCategory(props.id, getCategoriesofDay(day, daysValues)), getCategoriesofDay(day, daysValues), daysIds, day)
    //     })
    // })

    return (
        <div>
            Chart
        </div>
    )

}

export default CategoryChart
