import { ADD_DAY, ADD_CATEGORY, ADD_CELL } from "../actionTypes"
import {today} from "../../components/Today"

let dayId = today

/* STRUCTURE OF THE STATE

state = {
    days: [
        {
            day_id: "",
            categories: [
                {
                    category_name: "",
                    cells: []
                }
            ]
        }
    ]
} */

const initialState = {}

export default function(state = initialState, action) {
    switch(action.type) {
        case ADD_CELL: {
            return {
                ...state,
                day: {
                    ...state.day,
                    categories: [
                        ...state.day.categories,
                        {
                            cells: [
                                ...cells,
                                action.payload
                            ]
                        }
                    ]
                }
            }
        }
        default:
            return state
    }

}
