import { useSelector } from "react-redux"

const selectDays = state => state.categories.days

function ChartAll(props) {

    const days = useSelector(selectDays)

    // Array of all days [{},{},{}]
    const daysValues = Object.values(days)

    // Array of all ids ["", "", ""]
    const daysIds = daysValues.map((day) => day.id)
    //console.log(daysIds)

    // Array of arrays of objects - categories [[{}, {}], [{}, {}]]
    const categories = daysValues.map((day) => Object.values(day.categories))
    //console.log(categories)

    // Array of arrays of ids [["", "", ""], ["", "", ""]]
    const categoriesIds = categories.map((category) => category.map((cat) => cat.id))
    //console.log(categoriesIds)

    // Array of arrays of arrays of cells [[["",""],["",""]],[["",""],["",""]]]
    const cells = categories.map((category) => category.map((cat) => cat.cells))
    //console.log(cells)


    function getCategoriesofDay(day, data) {
        let categories = []
        data.forEach((item) => {
            console.log("day", day, "item", item)
            if (item.id === day) {
                categories = Object.values(item.categories).map((cat) => cat.id)
            }
        })
        console.log(categories)
        return categories
    }

    function getDay(data) {

        const days = Object.values(data).map((day) = day.id)
        return days
    }

    getCategoriesofDay(getDay[1], daysValues)

    return (
        <div>
            Chart
        </div>
    )

}

export default ChartAll
