const options = {
    chart: {
        toolbar: {
            show: false
        },
    },
    legend: {
        show: true
    },
    dataLabels: {
        enabled: false
    },
    plotOptions: {
        heatmap: {
            radius: 100,
            colorScale: {
                ranges: [{
                    from: 0,
                    to: 0,
                    color: "#FECACA"
                }, {
                    from: 1,
                    to: 2,
                    color: "#FDE68A"
                }]
            }
        }
    }
}
export default options
