let d = new Date()
console.log(d)
let year = d.getFullYear()
let month_num = d.getMonth()
console.log(month_num)
const months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"]
let month = months[month_num]
let day = d.getDate()
console.log(day)
let weekday_num = d.getDay()
const weekdays = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"]
let weekday = weekdays[weekday_num]

const today = weekday + ", " + day.toString() + " " + month + " " + year.toString()
const todayChart = day.toString() + "." + (month_num + 1).toString() + "."


console.log(today)

