const initialState = [{category:"", cells:[]}]

export default function(state = initialState, action) {
    switch(action.type) {
        case ADD_CATEGORY: {
            return [
                ...state,
                {
                    category: action.payload
                }
            ]
        }
        case ADD_CELL: {
            return [
                ...state,
                [{
                    ...state.category,
                    cells: [action.payload]
                }]
            ]
        }
        default:
            return state
    }

}
