import { ADD_DAY, ADD_CATEGORY, ADD_CELL, REMOVE_CELL } from "../actionTypes"

/* STRUCTURE OF THE STATE

state = {
    days: {
        [day_id]: {
            categories: {
                [category_name]: {
                    cells: []
                }
            }
        }
    }
} 
export default function(state = initialState, action) {
    switch(action.type) {
        case ADD_CELL: {
            return {
                ...state,
                days: {
                    ...state.days,
                    [action.payload.day_id]: {
                        ...state.days[action.payload.day_id],
                        categories: {
                            ...state.days[action.payload.day_id].categories,
                            [action.payload.category_name]: {
                                ...state.days[action.payload.day_id].categories[action.payload.category_name],
                                cells: [
                                    ...state.days[action.payload.day_id].categories[action.payload.category_name].cells,
                                    action.payload.cell_name
                                ]
                            }
                        }
                    }
                }
            }
        }

        default:
            return state
    }
}*/

/* STRUCTURE OF THE STATE - 2

state = {
    days: {
        byId: {
            "1.Jan.2021": {
                id: "1.Jan.2021",
                categories: [],
                cells: []
            }
        },
        allIds: {
            ["1.Jan.2021", 2.Jan.2021]
        }
    },
    categories: {
        byId: {
            "food": {
                id: "food",
                cells: []
            },
            "mood": {
                id: "mood",
                cells: []
            }
        },
        allIds: {
            ["food", "mood"]
        }
    },
    cells: {
        byId: {
            "bread": {
                id: "bread",
                category: "food"
            },
            "happy": {
                id: "happy",
                category: "mood"
            }
        },
        allIds: {
            ["bread", "happy"]
        }
    }
} */

/* STRUCTURE OF THE STATE - 3
state = {
    days: {
        [day_id]: {
            categories: {
                [category_name]: {
                    cells: []
                }
            }
        }
    },
    cells: {
        [cell_name]: {
            category: "",
            days: []
        }
    }
} */

const initialState = {
    days: {
        "0": {
            categories: {
                "test": {
                    cells: ["test"]
                }
            }
        }
    },
    cells: {
        "test": {
            category: "test",
            days: ["0"]
        }
    }
}

function addDay(state, action) {
    const { payload } = action
    const { dayId, categoryName, cellName } = payload
    return {
        ...state,
        [dayId]: {
            categories: {
                [categoryName]: {
                    cells: [cellName]
                }
            }
        }
    }
}

function addCategory(state, action) {
    const { payload } = action
    const { dayId, categoryName, cellName } = payload
    return {
        ...state,
        days: {
            ...state.days,
            [dayId]: {
                ...state.days[dayId],
                categories: {
                    ...state.days[dayId].categories,
                    [categoryName]: {
                        cells: [cellName]
                    }
                }
            }
        }
    }
}

function addCell(state, action) {
    const { payload } = action
    const { dayId, categoryName, cellName } = payload
    return {
        ...state,
        days: {
            ...state.days,
            [dayId]: {
                ...state.days[dayId],
                categories: {
                    ...state.days[dayId].categories,
                    [categoryName]: {
                        ...state.days[dayId].categories[categoryName],
                        cells: [
                            ...state.days[dayId].categories[categoryName].cells,
                            cellName
                        ]
                    }
                }
            }
        }
    }
}

export default function(state = initialState, action) {
    switch(action.type) {
        case ADD_CELL: {
            const { payload } = action
            const { dayId, categoryName, cellName } = payload
            return Object.assign({}, state, {
                days: state.days.map(day => {
                    if (day && day === dayId) {
                        categories: day.categories.map(category => {
                            if (category && category === categoryName) {
                                return addCell(state, action)
                            } else {
                                return addCategory(state, action)
                            }
                        }) 
                    } else {
                        return addDay(state, action)
                    }
                })
            })
        }

        default:
            return state
    }
}
