import dynamic from "next/dynamic"
import chartOptions from "../helpers/chartOptions"
import { storeDaysValue, storeDaysIds } from "./storeDays"
import { defaultDatas } from "../helpers/defaultDays"

const Chart = dynamic(() => import ("react-apexcharts"), {ssr: false})

function CategoryChart(props) {

    function cellHappenedToday(cell, chartDay) {
        return storeDaysValues.reduce((total, day) => {
            if (day.id === chartDay) {
                const storeCellsIds = Object.values(day.cells)
                if (storeCellsIds.includes(cell)) {
                    return total + 1
                }
            }               
            return total 
        }, 0)
    }

    // default chart series, generated for every cell
    const defaultSeries = props.defaultCells.map((cell) => {
        return {
            name: cell.name,
            data: defaultDatas
        }
    })

    const series = props.defaultCells.map((cell) => {
        return {
          name: cell.name,
          data: chartDays.map((day) => {
              return {
                  x: simplifyDate(day),
                  y: cellHappenedToday(cell.name, day)
              }
          })
        }
      })

    const chartWidth = (chartDays.length*15).toString()
    const chartHeight = (props.defaultCells.length*50).toString()

    return (
        <div className="flex flex-row">
            <div className={`text-lg w-1/5 font-semibold text-${props.color}-400`}>
                {props.id}
            </div>
            <div>
                <Chart options={chartOptions(props.color)} series={defaultSeries} type="heatmap" width={chartWidth} height={chartHeight}/>
            </div>

        </div>
    )
}

export default CategoryChart
